mapper.select:
select

from craw_page
where is_del=0

    and site_id=#{siteId}


    and page_type_id = #{pageTypeId}

select
count(*) from craw_page
where is_del=0

    and site_id=#{siteId}


    and page_type_id = #{pageTypeId}

mapper.update:
update craw_page
set is_del = 1, gmt_modified=now()
where   page_type_id = #{pageTypeId}
and site_id=#{siteId}

update craw_page
set is_del = 1, gmt_modified=now()
where id = #{id}
